const themeToggle=document.querySelector(".theme-toggle");if(themeToggle){themeToggle.addEventListener("click",switchTheme,false);}else{localStorage.removeItem("theme");}
function switchTheme(e){const currentTheme=document.documentElement.getAttribute("data-theme");if(currentTheme=="dark"){localStorage.setItem("theme","light");document.documentElement.setAttribute("data-theme","light")}else{localStorage.setItem("theme","dark");document.documentElement.setAttribute("data-theme","dark")}};const menuTrigger=document.querySelector(".menu-trigger");const menu=document.querySelector(".menu");const mobileQuery=getComputedStyle(document.body).getPropertyValue("--phoneWidth");const isMobile=()=>window.matchMedia(mobileQuery).matches;const isMobileMenu=()=>{menuTrigger&&menuTrigger.classList.toggle("hidden",!isMobile());menu&&menu.classList.toggle("hidden",isMobile());};isMobileMenu();menuTrigger&&menuTrigger.addEventListener("click",()=>menu&&menu.classList.toggle("hidden"));window.addEventListener("resize",isMobileMenu);